import { useState } from "react";
import { Bar } from "react-chartjs-2";

export default function Array() {
    const [shuffledArray, setShuffledArray] = useState<number[]>([3, 6, 2, 7, 8, 5, 1, 9, 4, 10]);
    const [sortType, setSortType] = useState<string>("simple");

    function shuffleArray(array: number[]) {
        let currentIdx: number = array.length;
        while (0 !== currentIdx) {
            let randomIdx: number = Math.floor(Math.random() * currentIdx);
            currentIdx -= 1;
            let tmp: number = array[currentIdx];
            array[currentIdx] = array[randomIdx];
            array[randomIdx] = tmp;
        }
        return array;
    }

    function changeSize(size: number) {
        const newArray: number[] = [];
        for (let i = 1; i <= size; i++) {
            newArray.push(i);
        }
        setShuffledArray(shuffleArray(newArray));
    }

    function buttonClass(type: string) {
        return type === sortType ? "btn btn-primary" : "btn btn-info";
    }

    const chartData = {
        labels: shuffledArray,
        datasets: [
            {
                label: "value",
                data: shuffledArray,
                backgroundColor: "#377E86",
            },
        ],
    };

    const chartOptions = {
        title: { display: true, text: "Sorting Algorithm", fontSize: 25 },
        maintainAspectRatio: false
    };

    return (
        <div>
            <Bar data={chartData} options={chartOptions} />
            <br />
            <h2>Array Size</h2>
            <input
                type="range"
                min="10"
                max="310"
                step="25"
                value={shuffledArray.length}
                className="slider"
                onChange={e => changeSize(parseInt(e.target.value))}
            />
            <br />
            <br />
            <h2 style={{ textTransform: "capitalize" }}>{sortType} Sort</h2>
            <div style={{ display: "flex", justifyContent: "center" }}>
                <button className={buttonClass("simple")} onClick={() => setSortType("simple")}>
                    Simple Sort
                </button>
                <button className={buttonClass("bubble")} onClick={() => setSortType("bubble")}>
                    Bubble Sort
                </button>
                <button className={buttonClass("selection")} onClick={() => setSortType("selection")}>
                    Selection Sort
                </button>
                <button className={buttonClass("merge")} onClick={() => setSortType("merge")}>
                    Merge Sort
                </button>
                <button className={buttonClass("quick")} onClick={() => setSortType("quick")}>
                    Quick Sort
                </button>
            </div>
        </div>
    );
}
