import { shuffleArray } from "./../helpers/shuffleArray";

interface props {
    sortArray: Function;
    setCanSort: Function;
    setDataArray: Function;
    dataArray: number[];
    canSort: boolean;
}

export default function ActionButtons({
    sortArray,
    setCanSort,
    setDataArray,
    dataArray,
    canSort,
}: props) {
    return (
        <div
            style={{
                margin: "15px 0 0",
                display: "flex",
                justifyContent: "center",
            }}>
            {canSort ? (
                <button
                    className={"btn btn-lg btn-info sort-btn"}
                    onClick={() => sortArray}>
                    Sort It!
                </button>
            ) : null}
            <button
                className={"btn btn-secondary sort-btn"}
                onClick={() => {
                    setCanSort(true);
                    setDataArray([...shuffleArray(dataArray)]);
                }}>
                Shuffle Array
            </button>
        </div>
    );
}
