export function calculateTimeDelay(length: number, sortType: string): number {
    let timeDelay: number;
    switch (true) {
        case length <= 25:
            timeDelay = 80;
            break;
        case length <= 45:
            timeDelay = 40;
            break;
        case length <= 65:
            timeDelay = 15;
            break;
        case length <= 85:
            if (sortType = "merge") timeDelay = 10
            else timeDelay = 2;
            break;
        default:
            if (sortType = "merge") timeDelay = 5
            else timeDelay = 1;
            break;
    }
    return timeDelay;
}
