export function calculateTimeDelay(length: number, sortType: string): number {
    console.log(sortType)
    let timeDelay: number;
    switch (true) {
        case length <= 25:
            if (sortType = "merge") timeDelay = 100
            else timeDelay = 80;
            break;
        case length <= 45:
            if (sortType = "merge") timeDelay = 80
            else timeDelay = 40;
            break;
        case length <= 65:
            if (sortType = "merge") timeDelay = 50
            else timeDelay = 15;
            break;
        case length <= 85:
            if (sortType = "merge") timeDelay = 30
            else timeDelay = 2;
            break;
        default:
            if (sortType = "merge") timeDelay = 15
            else timeDelay = 1;
            break;
    }
    return timeDelay;
}
