export function reverseArray(array: number[]) {
    let firstIdx: number = 0;
    let lastIdx: number = array.length - 1;
    while (firstIdx < lastIdx) {
        let temp = array[firstIdx];
        array[firstIdx] = array[lastIdx];
        array[lastIdx] = temp;
        firstIdx++;
        lastIdx--;
    }

    console.log("after", array);
    return array;
}
