import React, { useState } from "react";
import { Bar } from "react-chartjs-2";

export default function Array() {
    const array: number[] = [];
    const [arraySize, setArraySize] = useState(10);

    function shuffleArray(arr: number[]) {
        let currentIdx = arr.length;
        while (0 !== currentIdx) {
            let randomIdx = Math.floor(Math.random() * currentIdx);
            currentIdx -= 1;
            let tmp = arr[currentIdx];
            arr[currentIdx] = arr[randomIdx];
            array[randomIdx] = tmp;
        }
        return arr;
    }

    for (let i = 1; i <= arraySize; i++) {
        array.push(i);
    }

    const shuffledArray = shuffleArray(array);

    const chartData = {
        labels: shuffledArray,
        datasets: [
            {
                label: "value",
                data: shuffledArray,
                backgroundColor: "#377E86",
            },
        ],
    };
    const chartOptions = {
        title: { display: true, text: "Sorting Algorithm", fontSize: 25 },
    };

    return (
        <div>
            <Bar data={chartData} options={chartOptions} />
            <h4>Array Size</h4>
            <input type="range" min="10" max="310" step="25" className="slider"onChange={e => setArraySize(parseInt(e.target.value))} />
        </div>
    );
}
