import { useState, useRef, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import { shuffleArray } from "../Algorithms/shuffleArray";

export default function Array() {
    const [sortType, setSortType] = useState<string>("simple");
    const [dataArray, setDataArray] = useState<number[]>(shuffleArray([
        3,
        6,
        2,
        7,
        8,
        5,
        1,
        9,
        4,
        10,
    ]));
    const [chartData, setChartData] = useState<object>({
        labels: dataArray,
        datasets: [
            {
                label: "value",
                data: dataArray,
                backgroundColor: "#377E86",
            },
        ],
    });
    const animationRef = useRef<boolean>(true)
    const arrayRef = useRef<number[]>([]);
    arrayRef.current = dataArray;

    useEffect(() => {
        setChartData({
            labels: arrayRef.current,
            datasets: [
                {
                    label: "value",
                    data: arrayRef.current,
                    backgroundColor: "#377E86",
                },
            ],
        });
    }, [dataArray]);

    async function bubbleSort() {
        let isSorted: boolean = false;
        while (!isSorted) {
            for (let i = 0; i < dataArray.length; i++) {
                isSorted = true;

                for (let j = 1; j < dataArray.length - i; j++) {
                    if (dataArray[j] < dataArray[j - 1]) {
                        await swap(dataArray, j, j - 1);
                        isSorted = false;
                    }
                }
            }
        }
        return dataArray;
    }

    async function swap(array: number[], index1: number, index2: number) {
        await new Promise((resolve: any) =>
            setTimeout(async function () {
                const temp: number = array[index1];
                array[index1] = array[index2];
                array[index2] = temp;
                resolve();
            }, 1)
        );
        setDataArray([...array]);
    }

    function changeSize(size: number) {
        const newArray: number[] = [];
        for (let i = 1; i <= size; i++) {
            newArray.push(i);
        }
        setDataArray([...shuffleArray(newArray)]);
    }

    function buttonClass(type: string) {
        return type === sortType ? "btn btn-primary" : "btn btn-info";
    }

    return (
        <div>
            <h2 style={{ textTransform: "capitalize" }}>{sortType} Sort</h2>
            <div
                style={{
                    width: "70vw",
                    height: "60vh",
                    display: "flex",
                    justifyContent: "center",
                    margin: "auto",
                }}>
                <Bar
                    data={chartData}
                    options={{
                        maintainAspectRatio: false,
                        animation: animationRef.current,
                    }}
                />
            </div>
            <br />
            <h2>Array Size</h2>
            <input
                type="range"
                min="10"
                max="310"
                step="25"
                value={dataArray.length}
                className="slider"
                onChange={(e) => changeSize(parseInt(e.target.value))}
            />
            <br />
            <br />
            <div style={{ display: "flex", justifyContent: "center" }}>
                <button
                    className={buttonClass("simple")}
                    onClick={() => setSortType("simple")}>
                    Simple Sort
                </button>
                <button
                    className={buttonClass("bubble")}
                    onClick={() => setSortType("bubble")}>
                    Bubble Sort
                </button>
                <button
                    name="selection"
                    className={buttonClass("selection")}
                    onClick={() => setSortType("selection")}>
                    Selection Sort
                </button>
                <button
                    className={buttonClass("merge")}
                    onClick={() => setSortType("merge")}>
                    Merge Sort
                </button>
                <button
                    className={buttonClass("quick")}
                    onClick={() => setSortType("quick")}>
                    Quick Sort
                </button>
            </div>
            <button className={"btn btn-primary"} onClick={() => bubbleSort()}>
                Sort It!
            </button>
        </div>
    );
}
