import { useState, useEffect, useRef } from "react";
import { shuffleArray } from "../Algorithms/ShuffleArray";
import { reverseArray } from "../Algorithms/reverseArray";
import { BarChart, Tooltip, YAxis, XAxis, Bar } from "recharts";

export default function Array() {
    const [sortType, setSortType] = useState<string>("simple");
    const [shuffledArray, setShuffledArray] = useState<number[]>([
        3,
        6,
        2,
        7,
        8,
        5,
        1,
        9,
        4,
        10,
    ]);
    const chartDataRef: any = Object.keys(shuffledArray).map((key) => {
        return {
            key,
            value: key,
        };
    });
    function handleClick() {
        const reversed = reverseArray(shuffledArray)
        setShuffledArray(reversed)
    }

    function changeSize(size: number) {
        const newArray: number[] = [];
        for (let i = 1; i <= size; i++) {
            newArray.push(i);
        }
        setShuffledArray(shuffleArray(newArray));
    }

    function buttonClass(type: string) {
        return type === sortType ? "btn btn-primary" : "btn btn-info";
    }

    return (
        <div>
            <div
                style={{
                    width: "70vw",
                    height: "60vh",
                    display: "flex",
                    justifyContent: "center",
                    margin: "auto",
                }}>
                <h2 style={{ textTransform: "capitalize" }}>{sortType} Sort</h2>
                <BarChart
                    width={800}
                    height={300}
                    data={chartDataRef}
                    margin={{ top: 200, right: 20, left: 10, bottom: 5 }}>
                    <XAxis dataKey="key" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="key" fill="#313131" />
                </BarChart>
            </div>
            <br />
            <h2>Array Size</h2>
            <input
                type="range"
                min="10"
                max="310"
                step="25"
                value={shuffledArray.length}
                className="slider"
                onChange={(e) => changeSize(parseInt(e.target.value))}
            />
            <br />
            <br />
            <div style={{ display: "flex", justifyContent: "center" }}>
                <button
                    className={buttonClass("simple")}
                    onClick={() => setSortType("simple")}>
                    Simple Sort
                </button>
                <button
                    className={buttonClass("bubble")}
                    onClick={() => setSortType("bubble")}>
                    Bubble Sort
                </button>
                <button
                    className={buttonClass("selection")}
                    onClick={() => setSortType("selection")}>
                    Selection Sort
                </button>
                <button
                    className={buttonClass("merge")}
                    onClick={() => setSortType("merge")}>
                    Merge Sort
                </button>
                <button
                    className={buttonClass("quick")}
                    onClick={() => setSortType("quick")}>
                    Quick Sort
                </button>
            </div>
            <button onClick={handleClick}>click me!</button>
        </div>
    );
}
